mapper.update:
UPDATE article SET
    a_read_count=a_read_count+1
WHERE aid=#{id}

UPDATE article SET
    a_like_count=a_like_count+1
WHERE aid=#{id}

UPDATE article SET
    a_dislike_count=a_dislike_count+1
WHERE aid=#{id}

mapper.select:
SELECT *
FROM rating_article_history
WHERE uid=#{uid}
	AND aid=#{aid}

mapper.delete:
DELETE FROM collection
WHERE uid=#{uid}
	AND aid=#{aid}

